$(function () {
  $(".rate-star").rateYo({
    rating: 4.5,
    starWidth: "13px",
    spacing: "4px",
    readOnly: true,
  });

  $(".product__inner-box").slick({
    nextArrow: '<button class="product__slick-arrow product__slick-next"><img src="images/slider/arrow-right.png" alt="next arrow" /></button>',
    prevArrow: '<button class="product__slick-arrow product__slick-prev"><img src="images/slider/arrow-left.png" alt="prev arrow" /></button>',
    dots: false,
    autoplay: true,
  });

  $(".produce__filtering").on("click", function () {
    $(".produce__filtering-wrap").slideToggle();
  });

  var mixer = mixitup('.produce__items');

  $('.followers__items').slick({
    nextArrow: '<button class="followers__slick-arrow followers__slick-next"><img src="images/slider/arrow-right.png" alt="next arrow" /></button>',
    prevArrow: '<button class="followers__slick-arrow followers__slick-prev"><img src="images/slider/arrow-left.png" alt="prev arrow" /></button>',
    infinite: true,
    slidesToShow: 3,
    slidesToScroll: 3,
  });
  $(window).scroll(function () {

    var scrTop = $(window).scrollTop();

    if (scrTop > $('.progress__item-number').offset().top - $(window).height()) {

      numAnimate();

    }

  });

  function numAnimate() {

    $('.progress__item-number1').animate({
      num: 38436 - 1
    }, {

      duration: 3000,

      step: function (num) {

        this.innerHTML = (num + 1).toFixed(0)

      }

    });

    $('.progress__item-number2').animate({
      num: 3736 - 3
    }, {

      duration: 3000,

      step: function (num) {

        this.innerHTML = (num + 3).toFixed(0)

      }

    });

    $('.progress__item-number3').animate({
      num: 2736 - 3
    }, {

      duration: 3000,

      step: function (num) {

        this.innerHTML = (num + 3).toFixed(0)

      }

    });

    $('.progress__item-number4').animate({
      num: 25736 - 3
    }, {

      duration: 3000,

      step: function (num) {

        this.innerHTML = (num + 3).toFixed(0)

      }

    });
  };
});

$('.feedback__wrap').slick({
  nextArrow: '<button class="feedback__slick-arrow feedback__slick-next"><img src="images/feedback/arrow-right.png" alt="next arrow" /></button>',
  prevArrow: '<button class="feedback__slick-arrow feedback__slick-prev"><img src="images/feedback/arrow-left.png" alt="prev arrow" /></button>',
  infinite: false,
  speed: 700,
  slidesToShow: 2,
  slidesToScroll: 2,
  responsive: [{
      breakpoint: 1024,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2,
        infinite: true,
        dots: true
      }
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2
      }
    },
    {
      breakpoint: 480,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2
      }
    }
  ]
});